INSERT INTO `ecs_goods` VALUES ('174', '17', 'ECS000174', '现货加拿大原包装直邮Santevia便携式碱性水棒碱水棒负离子抗氧化', '+', '30', '0', '', '9999', '0.000', '178.79', '149.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;&nbsp;<span style=\"font-family: tahoma, arial, \'Hiragino Sans GB\', 宋体, sans-serif; line-height: 24px; text-indent: 5px; white-space: nowrap;\">品牌:&nbsp;Santevia</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 18px; line-height: 23.33px; font-family: kaiti_gb2312; color: rgb(11, 83, 148);\">【产品名称】：加拿大Santevia碱水棒 第二代</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-family: kaiti_gb2312;\"><span style=\"font-size: 18px; line-height: 23.33px;\">【商品材质】：</span></span></span><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-size: 18px;\"><span style=\"font-family: kaiti_gb2312;\">Santevia的碱水棒主要成分是两种天然矿石：电气石和麦饭石</span></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-family: kaiti_gb2312;\"><span style=\"font-size: 18px;\">【商品产地】：加拿大</span></span></span></p>\r\n<div style=\"margin: 0px; padding: 0px; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14.44px; line-height: 23.33px;\"><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-family: kaiti_gb2312;\"><span style=\"font-size: 18px;\">【产品特点】：</span></span></span></div>\r\n<p style=\"margin: 1.12em 0px; padding: 0px; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-size: 18px;\">Santevia 的碱水棒主要成分是两种天然矿石：电气石和麦饭石。这两种矿石用水浸泡后，可以容出钾、钠、钙、镁、硅、猛、钛、磷等人体所必需的近20种元素和矿物质， 可达到矿泉水的标准。可以补充人体所需矿物质和多种微量元素，为有机体提供了无机营养成分，调节新陈代谢，提高免疫力。</span></span></p>\r\n<p style=\"margin: 1.12em 0px; padding: 0px; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; text-align: center;\"><img src=\"https://img.alicdn.com/imgextra/i2/1980686535/TB2HSPSiVXXXXbnXXXXXXXXXXXX_!!1980686535.jpg\" alt=\"\" width=\"550\" height=\"550\" style=\"font-family: Arial, Verdana, sans-serif; font-size: 12px; text-align: center;\" /></p>\r\n<p style=\"margin: 1.12em 0px; padding: 0px; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-size: 18px;\"><span style=\"color: rgb(255, 130, 51);\">碱水棒的正确使用方法及注意事项：</span><br />\r\n<span style=\"color: rgb(255, 99, 0);\">使用前，碱水棒的表面可能会有少量天然矿物质粉末，用清水冲洗2-3次即可。也可使用热水冲洗，激活棒内天然矿物质，效果更佳。<br />\r\n<br />\r\n为保证碱水棒食用功效，建议在饮用水中放置使用，搅拌后静置3-5分钟，然后取出。请勿直接放入茶叶水，碳酸饮料，牛奶，咖啡等饮料中使用。<br />\r\n<br />\r\n若需加热货冰冻饮用水，请在煮水或冰冻前放入碱水棒，搅拌后静置3-5分钟，取出碱水棒后煮沸或冰冻。<br />\r\n<br />\r\n使用完毕后请自然晾干（避免暴晒），然后放入包装管内保存或携带。</span><br />\r\n<br />\r\n<span style=\"color: rgb(183, 153, 224);\">碱水棒的正常使用时间，若用于自来水中，可连续使用三个月，大约250次；若用于纯净水之中，可连续使用一年，约可使用1000次。</span><br />\r\n<br />\r\n<span style=\"color: rgb(255, 0, 0);\">深层清洗：浸泡于热水中5-10分钟。 取出碱水棒，自然晾干！</span></span></span></p>\r\n<p style=\"text-align: center;\">&nbsp;<img src=\"/images/upload/Image/碱水棒2.jpg\" width=\"550\" height=\"550\" alt=\"\" /></p>\r\n<p style=\"margin: 1.12em 0px; padding: 0px; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"color: rgb(11, 83, 148);\"><span style=\"font-size: 18px;\">【产品小百科】</span></span></p>\r\n<p><span style=\"font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: rgb(11, 83, 148);\"><span style=\"font-size: 18px;\">饮用碱性水：<br />\r\n<br />\r\n1。碱性水适合宝宝冲泡奶粉，可以补充钙质，增强免疫力<br />\r\n<br />\r\n2。碱性水泡茶后，使血液中酒精成分加快解酒<br />\r\n<br />\r\n3。碱性水烹饪食物,更能还原食材自然滋味<br />\r\n<br />\r\n4。碱性水洗净过的蔬果放入冰箱后能延长保鲜期<br />\r\n<br />\r\n5。缓解高血压,高血脂.II型糖尿病<br />\r\n<br />\r\n6。增强肌肤的弹性,延缓皮肤衰老<br />\r\n<br />\r\n7。缓解便秘,改善痛风症状</span></span></p>', 'images/201601/thumb_img/174_thumb_G_1453789555137.jpg', 'images/201601/goods_img/174_G_1453789555432.jpg', 'images/201601/source_img/174_G_1453789555281.jpg', '1', '', '1', '1', '0', '1', '1453789555', '100', '0', '0', '1', '1', '0', '0', '1453789920', '0', '', '-1', '-1', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
